import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { Container, Row, Col, Card, Button, Alert, Spinner, Badge } from 'react-bootstrap';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import { saveAs } from 'file-saver';
import { FiUpload, FiDownload, FiDatabase, FiSun, FiMoon, FiClock, FiInfo } from 'react-icons/fi';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

// Import new components
import ProjectFilters from './components/ProjectFilters';
import DataStats from './components/DataStats';

function App() {
  const [fileData, setFileData] = useState(null);
  const [predictions, setPredictions] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [darkMode, setDarkMode] = useState(() => {
    // Persist mode in localStorage
    return localStorage.getItem('darkMode') === 'true';
  });
  const [showPreview, setShowPreview] = useState(true);
  const [fileInfo, setFileInfo] = useState(null);
  const [storageStats, setStorageStats] = useState(null);

  // Project filtering state
  const [selectedProject, setSelectedProject] = useState(null);
  const [availableProjects, setAvailableProjects] = useState([]);
  const [fullDataset, setFullDataset] = useState(null);
  const [filteredData, setFilteredData] = useState(null);
  const [filterLoading, setFilterLoading] = useState(false);

  // Force re-render state
  const [renderKey, setRenderKey] = useState(0);

  useEffect(() => {
    // Fetch available models on component mount
    fetchAvailableModels();
    fetchStorageStats();
  }, []);

  useEffect(() => {
    document.body.classList.toggle('dark-mode', darkMode);
    document.body.classList.toggle('light-mode', !darkMode);
    localStorage.setItem('darkMode', darkMode);
  }, [darkMode]);

  // Debug useEffect to monitor state changes
  useEffect(() => {
    console.log('State changed:', {
      fileData: !!fileData,
      showPreview,
      fullDatasetLength: fullDataset?.length || 0,
      filteredDataLength: filteredData?.length || 0,
      displayDataLength: displayData?.length || 0,
      selectedProject,
      availableProjectsCount: availableProjects?.length || 0
    });
  }, [fileData, showPreview, fullDataset, filteredData, displayData, selectedProject, availableProjects]);

  // Force re-render when data changes
  useEffect(() => {
    if (fileData && fullDataset) {
      console.log('Data loaded, forcing re-render');
      setRenderKey(prev => prev + 1);
    }
  }, [fileData, fullDataset]);

  const fetchAvailableModels = async () => {
    try {
      const response = await axios.get('/api/models');
      // We'll use this data later if needed
      console.log('Available models:', response.data.available_models);
    } catch (error) {
      console.error('Error fetching models:', error);
    }
  };

  const fetchStorageStats = async () => {
    try {
      const response = await axios.get('/api/storage/stats');
      setStorageStats(response.data);
    } catch (error) {
      console.error('Error fetching storage stats:', error);
    }
  };

  // Extract unique projects from data
  const extractProjects = useCallback((data) => {
    if (!data || data.length === 0) return [];

    const projects = new Set();
    data.forEach(row => {
      if (row.projectid) {
        projects.add(row.projectid.toString());
      }
    });

    return Array.from(projects);
  }, []);

  // Filter data by project
  const filterDataByProject = useCallback((data, projectId) => {
    if (!data || !projectId) return data;

    setFilterLoading(true);

    // Use setTimeout to prevent UI blocking for large datasets
    setTimeout(() => {
      const filtered = data.filter(row => 
        row.projectid && row.projectid.toString() === projectId.toString()
      );
      setFilteredData(filtered);
      setFilterLoading(false);
    }, 0);
  }, []);

  // Handle project selection
  const handleProjectFilter = useCallback((projectId) => {
    setSelectedProject(projectId);
    if (projectId) {
      filterDataByProject(fullDataset, projectId);
    } else {
      setFilteredData(fullDataset);
    }
  }, [fullDataset, filterDataByProject]);

  // Handle filter reset
  const handleFilterReset = useCallback(() => {
    setSelectedProject(null);
    setFilteredData(fullDataset);
  }, [fullDataset]);

  // Memoized current data (either filtered or full dataset)
  const currentData = useMemo(() => {
    console.log('Computing currentData:', {
      selectedProject,
      filteredDataLength: filteredData?.length || 0,
      fullDatasetLength: fullDataset?.length || 0
    });

    const result = selectedProject ? filteredData : fullDataset;
    console.log('currentData result:', result?.length || 0);
    return result;
  }, [selectedProject, filteredData, fullDataset]);

  // Memoized display data for preview (first and last 5 rows)
  const displayData = useMemo(() => {
    console.log('Computing display data:', {
      currentData: currentData?.length,
      selectedProject,
      filteredData: filteredData?.length,
      fullDataset: fullDataset?.length
    });

    if (!currentData || currentData.length === 0) {
      console.log('No current data available');
      return [];
    }

    if (currentData.length <= 10) {
      console.log('Using all data (<= 10 rows):', currentData.length);
      return currentData;
    }

    const first5 = currentData.slice(0, 5);
    const last5 = currentData.slice(-5);
    const result = [...first5, ...last5];
    console.log('Using first 5 + last 5 rows:', result.length);
    return result;
  }, [currentData]);

  const onDrop = async (acceptedFiles) => {
    if (acceptedFiles.length === 0) return;

    const file = acceptedFiles[0];
    setLoading(true);
    setError(null);

    // First check if backend is accessible
    try {
      await axios.get('/api/models', { timeout: 5000 });
    } catch (backendError) {
      setError('Backend server is not accessible. Please ensure the backend is running on port 5000.');
      setLoading(false);
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      console.log('Uploading file:', file.name, 'Size:', file.size);

      const response = await axios.post('/api/upload_uuid', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
        timeout: 30000, // 30 second timeout
      });

      console.log('Upload successful:', response.data);

      // Start with preview data
      let fullDatasetData = response.data.preview;

      // Only try to get full dataset for smaller files (< 10MB)
      if (file.size < 10 * 1024 * 1024) {
        try {
          console.log('Attempting to fetch full dataset...');
          const fullDatasetResponse = await axios.get(`/api/full_dataset/${response.data.filename}`, {
            timeout: 60000, // 60 second timeout for large datasets
          });
          fullDatasetData = fullDatasetResponse.data;
          console.log('Full dataset loaded:', fullDatasetData.length, 'rows');
        } catch (fullDatasetError) {
          console.warn('Could not load full dataset, using preview data for filtering:', fullDatasetError);
          // Continue with preview data
        }
      } else {
        console.log('File too large, using preview data only');
      }

      setFullDataset(fullDatasetData);
      setFilteredData(fullDatasetData);

      // Extract available projects from the data we have
      const projects = extractProjects(fullDatasetData);
      setAvailableProjects(projects);

      setFileData({
        filename: response.data.filename,
        preview: response.data.preview,
        originalName: file.name,
      });
      setFileInfo(response.data.file_info);
      setShowPreview(true);

      // Reset filter state
      setSelectedProject(null);

      // Force re-render to ensure UI updates
      setRenderKey(prev => prev + 1);

      console.log('Upload completed successfully:', {
        filename: response.data.filename,
        previewRows: response.data.preview.length,
        fullDatasetRows: fullDatasetData.length,
        originalName: file.name,
        fileInfo: response.data.file_info,
        availableProjects: projects,
      });

      // Debug state after setting
      console.log('State after upload:', {
        fileData: !!fileData,
        showPreview,
        fullDatasetLength: fullDatasetData.length,
        filteredDataLength: fullDatasetData.length,
        availableProjectsCount: projects.length
      });
    } catch (error) {
      console.error('Upload error:', error);
      let errorMessage = 'Error uploading file';

      if (error.response) {
        // Server responded with error
        errorMessage = error.response.data?.error || `Server error: ${error.response.status}`;
      } else if (error.request) {
        // Network error
        errorMessage = 'Network error: Could not connect to server. Please check if the backend is running.';
      } else {
        // Other error
        errorMessage = error.message || 'Unknown error occurred';
      }

      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'text/csv': ['.csv'],
      'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet': ['.xlsx'],
      'application/vnd.ms-excel': ['.xls'],
    },
    multiple: false,
  });

  const generatePredictions = async () => {
    if (!fileData) return;
    setLoading(true);
    setError(null);
    try {
      const response = await axios.post('/api/predict_workflow', {
        filename: fileData.filename,
      });

      // Try to get the full prediction dataset
      let fullPredictionData = response.data.preview.final; // Fallback to preview
      try {
        const fullPredictionResponse = await axios.get(`/api/full_dataset/${response.data.results_filename}`);
        fullPredictionData = fullPredictionResponse.data;
      } catch (fullPredictionError) {
        console.warn('Could not load full prediction dataset, using preview data:', fullPredictionError);
        // Continue with preview data
      }

      setFullDataset(fullPredictionData);
      setFilteredData(fullPredictionData);

      // Re-extract projects from prediction data (in case preprocessing changed project IDs)
      const projects = extractProjects(fullPredictionData);
      setAvailableProjects(projects);

      setPredictions(response.data);
      setShowPreview(false); // Hide preview after prediction

      // Reset filter state for prediction results
      setSelectedProject(null);
    } catch (error) {
      setError(error.response?.data?.error || 'Error generating predictions');
    } finally {
      setLoading(false);
    }
  };

  const exportResults = async (format) => {
    if (!predictions) return;

    try {
      if (format === 'json') {
        const response = await axios.get(`/api/export/${format}/${predictions.results_filename}`);
        const blob = new Blob([JSON.stringify(response.data, null, 2)], {
          type: 'application/json',
        });
        saveAs(blob, `predictions_${fileData.filename.split('.')[0]}.json`);
      } else {
        const response = await axios.get(`/api/export/${format}/${predictions.results_filename}`, {
          responseType: 'blob',
        });
        const extension = format === 'csv' ? 'csv' : 'xlsx';
        saveAs(response.data, `predictions_${fileData.filename.split('.')[0]}.${extension}`);
      }
    } catch (error) {
      setError('Error exporting results');
    }
  };

  // Export filtered data
  const exportFilteredData = async (format) => {
    if (!currentData || currentData.length === 0) {
      setError('No data to export');
      return;
    }

    try {
      let blob, filename;

      if (format === 'json') {
        const jsonData = JSON.stringify(currentData, null, 2);
        blob = new Blob([jsonData], { type: 'application/json' });
        filename = `filtered_data_${selectedProject || 'all'}.json`;
      } else if (format === 'csv') {
        // Convert data to CSV
        const headers = Object.keys(currentData[0]);
        const csvContent = [
          headers.join(','),
          ...currentData.map(row => 
            headers.map(header => {
              const value = row[header];
              // Escape commas and quotes in CSV
              if (typeof value === 'string' && (value.includes(',') || value.includes('"'))) {
                return `"${value.replace(/"/g, '""')}"`;
              }
              return value || '';
            }).join(',')
          )
        ].join('\n');

        blob = new Blob([csvContent], { type: 'text/csv' });
        filename = `filtered_data_${selectedProject || 'all'}.csv`;
      } else if (format === 'xlsx') {
        // For Excel, we'll need to use a library or convert to CSV first
        // For now, let's use CSV format
        const headers = Object.keys(currentData[0]);
        const csvContent = [
          headers.join(','),
          ...currentData.map(row => 
            headers.map(header => {
              const value = row[header];
              if (typeof value === 'string' && (value.includes(',') || value.includes('"'))) {
                return `"${value.replace(/"/g, '""')}"`;
              }
              return value || '';
            }).join(',')
          )
        ].join('\n');

        blob = new Blob([csvContent], { type: 'text/csv' });
        filename = `filtered_data_${selectedProject || 'all'}.csv`;
      }

      if (blob) {
        saveAs(blob, filename);
      }
    } catch (error) {
      setError('Error exporting filtered data');
    }
  };

  const cleanup = async () => {
    setFileData(null);
    setPredictions(null);
    setFileInfo(null);
    setError(null);
    setShowPreview(true);

    // Reset filter state
    setSelectedProject(null);
    setAvailableProjects([]);
    setFullDataset(null);
    setFilteredData(null);
    setFilterLoading(false);
  };

  const renderPreviewTable = (data, title) => {
    if (!data || data.length === 0) return null;

    let columns = Object.keys(data[0]);
    if (columns.includes('has_booked_prediction')) {
      columns = columns.filter(c => c !== 'has_booked_prediction').concat(['has_booked_prediction']);
    }

    return (
      <Card className="mb-4">
        <Card.Header>
          <h5 className="mb-0">
            {title}
            {filterLoading && (
              <Spinner animation="border" size="sm" className="ms-2" />
            )}
          </h5>
        </Card.Header>
        <Card.Body className="p-0">
          <div className="table-responsive">
            <table className="table table-striped table-hover mb-0">
              <thead className="table-primary">
                <tr>
                  {columns.map((column) => (
                    <th key={column}>{column}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {data.map((row, index) => (
                  <tr key={index}>
                    {columns.map((column) => (
                      <td key={column}>
                        {typeof row[column] === 'boolean' 
                          ? (row[column] ? 'Yes' : 'No')
                          : row[column]?.toString() || ''}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </Card.Body>
      </Card>
    );
  };

  const renderFileRetentionInfo = () => {
    if (!fileInfo) return null;

    const uploadDate = new Date(fileInfo.upload_timestamp);
    const deletionDate = new Date(fileInfo.deletion_date);
    const daysLeft = Math.ceil((deletionDate - new Date()) / (1000 * 60 * 60 * 24));

    return (
      <Card className="mb-4">
        <Card.Header>
          <h5 className="mb-0">
            <FiClock className="me-2" />
            File Retention Information
          </h5>
        </Card.Header>
        <Card.Body>
          <Row>
            <Col md={6}>
              <p><strong>Upload Time:</strong> {uploadDate.toLocaleString()}</p>
              <p><strong>Deletion Date:</strong> {deletionDate.toLocaleString()}</p>
            </Col>
            <Col md={6}>
              <p><strong>Days Remaining:</strong> 
                <Badge 
                  bg={daysLeft > 30 ? 'success' : daysLeft > 7 ? 'warning' : 'danger'}
                  className="ms-2"
                >
                  {daysLeft} days
                </Badge>
              </p>
              <p><strong>Status:</strong> 
                <Badge bg="info" className="ms-2">{fileInfo.status}</Badge>
              </p>
            </Col>
          </Row>
        </Card.Body>
      </Card>
    );
  };

  const renderStorageStats = () => {
    if (!storageStats) return null;

    return (
      <Card className="mb-4">
        <Card.Header>
          <h5 className="mb-0">
            <FiInfo className="me-2" />
            Storage Statistics
          </h5>
        </Card.Header>
        <Card.Body>
          <Row>
            <Col md={3}>
              <div className="text-center">
                <div className="stats-number text-primary">{storageStats.total_files}</div>
                <div className="text-muted">Total Files</div>
              </div>
            </Col>
            <Col md={3}>
              <div className="text-center">
                <div className="stats-number text-info">{storageStats.total_size_mb} MB</div>
                <div className="text-muted">Total Size</div>
              </div>
            </Col>
            <Col md={3}>
              <div className="text-center">
                <div className="stats-number text-success">{storageStats.retention_days}</div>
                <div className="text-muted">Retention Days</div>
              </div>
            </Col>
            <Col md={3}>
              <div className="text-center">
                <Button 
                  variant="outline-secondary" 
                  size="sm"
                  onClick={fetchStorageStats}
                >
                  <FiInfo className="me-1" />
                  Refresh
                </Button>
              </div>
            </Col>
          </Row>
        </Card.Body>
      </Card>
    );
  };

  return (
    <Container fluid className="upload-container">
      <button
        className="theme-toggle"
        onClick={() => setDarkMode((dm) => !dm)}
        title={darkMode ? 'Switch to Light Mode' : 'Switch to Dark Mode'}
      >
        {darkMode ? <FiMoon /> : <FiSun />}
      </button>
      <Row className="justify-content-center">
        <Col lg={10}>
          <div className="text-center mb-4">
            <h1 className="display-4 text-primary">ML Prediction System</h1>
            <p className="lead text-muted">
              Upload your CSV or Excel files to get predictions using our pre-trained models
            </p>
          </div>

          {error && (
            <Alert variant="danger" dismissible onClose={() => setError(null)}>
              <div>
                <strong>Upload Error:</strong>
                <br />
                {error}
                <br />
                <small className="text-muted mt-2 d-block">
                  Please check:
                  <ul className="mb-0 mt-1">
                    <li>File format is CSV, XLSX, or XLS</li>
                    <li>File contains a "projectid" column</li>
                    <li>Backend server is running on port 5000</li>
                    <li>File size is reasonable (&lt; 100MB)</li>
                  </ul>
                </small>
                <Button 
                  variant="outline-danger" 
                  size="sm" 
                  className="mt-2"
                  onClick={() => setError(null)}
                >
                  Try Again
                </Button>
              </div>
            </Alert>
          )}

          {!fileData && (
            <Card>
              <Card.Body>
                <div
                  {...getRootProps()}
                  className={`upload-area ${isDragActive ? 'dragover' : ''}`}
                >
                  <input {...getInputProps()} />
                  <FiUpload size={48} className="text-primary mb-3" />
                  <h4>Drop your file here, or click to select</h4>
                  <p className="text-muted">
                    Supports CSV and Excel files (max 100,000 rows) - Files are automatically converted to CSV for faster processing
                  </p>
                  <p className="text-muted small">
                    File must contain a "projectid" column
                  </p>
                  <div className="file-size-info">
                    <small>
                      💡 Tip: For files larger than 10MB, only preview data will be available for filtering
                    </small>
                  </div>
                </div>
              </Card.Body>
            </Card>
          )}

          {loading && (
            <div className="loading-spinner">
              <div className="text-center">
                <Spinner animation="border" role="status" className="mb-3">
                  <span className="visually-hidden">Loading...</span>
                </Spinner>
                <p>Processing your file...</p>
                <small className="text-muted">
                  This may take a few moments for large files
                </small>
              </div>
            </div>
          )}

          {fileData && !loading && showPreview && (
            <>
              <Card className="mb-4">
                <Card.Header>
                  <h5 className="mb-0">File Information</h5>
                </Card.Header>
                <Card.Body>
                  <Row>
                    <Col md={6}>
                      <p><strong>Filename:</strong> {fileData.originalName || fileData.filename}</p>
                      <p><strong>Data Rows:</strong> {fullDataset?.length || fileData.preview?.length || 0}</p>
                      <p><strong>Display Rows:</strong> {displayData?.length || 0}</p>
                    </Col>
                    <Col md={6}>
                      <Button
                        variant="primary"
                        onClick={generatePredictions}
                        disabled={loading || !fileData}
                        className="me-2"
                      >
                        <FiDatabase className="me-2" />
                        Generate Predictions
                      </Button>
                      <Button variant="outline-secondary" onClick={cleanup}>
                        Upload New File
                      </Button>
                      <Button 
                        variant="outline-info" 
                        size="sm" 
                        onClick={() => {
                          console.log('Manual debug check:', {
                            fileData: !!fileData,
                            showPreview,
                            fullDatasetLength: fullDataset?.length || 0,
                            filteredDataLength: filteredData?.length || 0,
                            displayDataLength: displayData?.length || 0
                          });
                        }}
                        className="ms-2"
                      >
                        Debug
                      </Button>
                      <Button 
                        variant="outline-warning" 
                        size="sm" 
                        onClick={() => {
                          // Test setting data manually
                          const testData = [
                            { projectid: 1, name: 'Test 1' },
                            { projectid: 2, name: 'Test 2' },
                            { projectid: 3, name: 'Test 3' }
                          ];
                          setFullDataset(testData);
                          setFilteredData(testData);
                          setFileData({ filename: 'test.csv', preview: testData, originalName: 'test.csv' });
                          setShowPreview(true);
                          console.log('Manual test data set');
                        }}
                        className="ms-2"
                      >
                        Test Data
                      </Button>
                    </Col>
                  </Row>
                </Card.Body>
              </Card>

              {/* Project Filters */}
              <ProjectFilters
                projects={availableProjects}
                selectedProject={selectedProject}
                onSelect={handleProjectFilter}
                onReset={handleFilterReset}
                disabled={loading}
                className="mb-4"
              />

              {/* Data Statistics */}
              <DataStats
                data={currentData}
                title={selectedProject ? `Project ${selectedProject} Statistics` : "Full Dataset Statistics"}
                className="mb-4"
              />

              {/* Preview Table */}
              {(!displayData || displayData.length === 0) ? (
                <Alert variant="warning">
                  <strong>No preview data available for this file.</strong>
                  <br />
                  <small className="text-muted">
                    Debug Info: fileData={!!fileData}, showPreview={showPreview}, 
                    fullDataset={fullDataset?.length || 0}, 
                    filteredData={filteredData?.length || 0}, 
                    displayData={displayData?.length || 0}
                  </small>
                </Alert>
              ) : (
                renderPreviewTable(displayData, `Data Preview ${selectedProject ? `(Project ${selectedProject})` : '(All Projects)'}`)
              )}
            </>
          )}

          {predictions && !loading && (
            <>
              <Card className="mb-4">
                <Card.Header>
                  <h5 className="mb-0">Prediction Results</h5>
                </Card.Header>
                <Card.Body>
                  <Row>
                    <Col md={6}>
                      <p><strong>Rows Processed:</strong> {predictions.stats?.rows_processed ?? '-'}</p>
                      <p><strong>Prediction Distribution:</strong> {JSON.stringify(predictions.stats?.prediction_distribution ?? {})}</p>
                    </Col>
                  </Row>
                </Card.Body>
              </Card>

              {/* Project Filters for Prediction Results */}
              <ProjectFilters
                projects={availableProjects}
                selectedProject={selectedProject}
                onSelect={handleProjectFilter}
                onReset={handleFilterReset}
                disabled={loading}
                className="mb-4"
              />

              {/* Prediction Data Statistics */}
              <DataStats
                data={currentData}
                title={selectedProject ? `Project ${selectedProject} Prediction Statistics` : "Full Prediction Statistics"}
                className="mb-4"
              />

              {/* Final Prediction Preview */}
              <h5>Final Prediction Preview</h5>
              {renderPreviewTable(displayData, `Prediction Results ${selectedProject ? `(Project ${selectedProject})` : '(All Projects)'}`)}

              <Card>
                <Card.Header>
                  <h5 className="mb-0">
                    <FiDownload className="me-2" />
                    Export Results
                  </h5>
                </Card.Header>
                <Card.Body>
                  <Row>
                    <Col md={6}>
                      <h6>Export Full Prediction Results</h6>
                      <div className="d-flex flex-wrap gap-2 mb-3">
                        <Button
                          variant="outline-primary"
                          size="sm"
                          onClick={() => exportResults('csv')}
                          className="btn-export"
                        >
                          Export as CSV
                        </Button>
                        <Button
                          variant="outline-success"
                          size="sm"
                          onClick={() => exportResults('xlsx')}
                          className="btn-export"
                        >
                          Export as Excel
                        </Button>
                        <Button
                          variant="outline-info"
                          size="sm"
                          onClick={() => exportResults('json')}
                          className="btn-export"
                        >
                          Export as JSON
                        </Button>
                      </div>
                    </Col>
                    <Col md={6}>
                      <h6>Export Filtered Data</h6>
                      <div className="d-flex flex-wrap gap-2">
                        <Button
                          variant="outline-warning"
                          size="sm"
                          onClick={() => exportFilteredData('csv')}
                          className="btn-export"
                          disabled={!currentData || currentData.length === 0}
                        >
                          Export Filtered CSV
                        </Button>
                        <Button
                          variant="outline-secondary"
                          size="sm"
                          onClick={() => exportFilteredData('json')}
                          className="btn-export"
                          disabled={!currentData || currentData.length === 0}
                        >
                          Export Filtered JSON
                        </Button>
                      </div>
                      {selectedProject && (
                        <small className="text-muted d-block mt-1">
                          Exporting data for Project {selectedProject}
                        </small>
                      )}
                    </Col>
                  </Row>
                </Card.Body>
              </Card>
              {/* Upload New File button at the bottom */}
              <div style={{ display: 'flex', justifyContent: 'center', marginTop: '2rem', marginBottom: '1rem' }}>
                <Button
                  variant="secondary"
                  size="lg"
                  onClick={cleanup}
                  style={{ minWidth: 220, fontWeight: 600 }}
                >
                  Upload New File
                </Button>
              </div>
            </>
          )}

          {fileInfo && (
            <>
              {renderFileRetentionInfo()}
              {renderStorageStats()}
            </>
          )}
        </Col>
      </Row>
    </Container>
  );
}

export default App;
