import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Alert, Spinner, Nav, Tab } from 'react-bootstrap';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import { saveAs } from 'file-saver';
import { FiUpload, FiDownload, FiSun, FiMoon } from 'react-icons/fi';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

function App() {
  // State
  const [uuid, setUuid] = useState(null);
  const [filename, setFilename] = useState('');
  const [rawPreview, setRawPreview] = useState(null);
  const [processedPreview, setProcessedPreview] = useState(null);
  const [finalResults, setFinalResults] = useState(null);
  const [exportLinks, setExportLinks] = useState({});
  const [stats, setStats] = useState({});
  const [loading, setLoading] = useState(false);
  const [progressMsg, setProgressMsg] = useState('');
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('raw');
  const [darkMode, setDarkMode] = useState(() => localStorage.getItem('darkMode') === 'true');

  // Theme effect
  useEffect(() => {
    document.body.classList.toggle('dark-mode', darkMode);
    document.body.classList.toggle('light-mode', !darkMode);
    localStorage.setItem('darkMode', darkMode);
  }, [darkMode]);

  // File upload handler
  const onDrop = async (acceptedFiles) => {
    if (acceptedFiles.length === 0) return;
    const file = acceptedFiles[0];
    setLoading(true);
    setError(null);
    setProgressMsg('Uploading and previewing file...');
    setUuid(null);
    setFilename('');
    setRawPreview(null);
    setProcessedPreview(null);
    setFinalResults(null);
    setExportLinks({});
    setStats({});
    setActiveTab('raw');

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('/api/upload_uuid', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setUuid(response.data.uuid);
      setFilename(response.data.filename);
      setRawPreview(response.data.raw_preview);
      setStats(response.data.stats || {});
      setProgressMsg('');
    } catch (err) {
      setError(err.response?.data?.error || 'Error uploading file');
      setProgressMsg('');
    } finally {
      setLoading(false);
    }
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'text/csv': ['.csv'],
      'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet': ['.xlsx'],
      'application/vnd.ms-excel': ['.xls'],
    },
    multiple: false,
    disabled: loading,
  });

  // Prediction workflow
  const handlePredict = async () => {
    if (!uuid) return;
    setLoading(true);
    setError(null);
    setProgressMsg('Processing and generating predictions...');
    setProcessedPreview(null);
    setFinalResults(null);
    setExportLinks({});
    setStats({});
    setActiveTab('final');

    try {
      const response = await axios.post('/api/predict_workflow', { uuid });
      setProcessedPreview(response.data.processed_preview);
      setFinalResults(response.data.final_results);
      setExportLinks(response.data.export_links || {});
      setStats(response.data.stats || {});
      setProgressMsg('');
    } catch (err) {
      setError(err.response?.data?.error || 'Error generating predictions');
      setProgressMsg('');
    } finally {
      setLoading(false);
    }
  };

  // Export handler
  const handleExport = async (type) => {
    if (!exportLinks[type]) return;
    setLoading(true);
    setProgressMsg('Exporting...');
    try {
      const url = exportLinks[type];
      const response = await axios.get(url, { responseType: 'blob' });
      const ext = type === 'csv' ? 'csv' : 'xlsx';
      saveAs(response.data, `prediction_results.${ext}`);
    } catch (err) {
      setError('Error exporting results');
    } finally {
      setLoading(false);
      setProgressMsg('');
    }
  };

  // Reset workflow
  const handleReset = () => {
    setUuid(null);
    setFilename('');
    setRawPreview(null);
    setProcessedPreview(null);
    setFinalResults(null);
    setExportLinks({});
    setStats({});
    setError(null);
    setActiveTab('raw');
  };

  // Table renderer
  const renderTable = (data, title) => {
    if (!data || data.length === 0) return <div className="text-muted">No data to display.</div>;
    let columns = Object.keys(data[0]);
    // Move has_booked_prediction to end if present
    if (columns.includes('has_booked_prediction')) {
      columns = columns.filter(c => c !== 'has_booked_prediction').concat(['has_booked_prediction']);
    }
    return (
      <Card className="mb-4">
        <Card.Header>
          <h5 className="mb-0">{title}</h5>
        </Card.Header>
        <Card.Body className="p-0">
          <div className="table-responsive">
            <table className="table table-striped table-hover mb-0">
              <thead className="table-primary">
                <tr>
                  {columns.map((col) => <th key={col}>{col}</th>)}
                </tr>
              </thead>
              <tbody>
                {data.map((row, idx) => (
                  <tr key={idx}>
                    {columns.map((col) => (
                      <td key={col}>
                        {typeof row[col] === 'boolean'
                          ? (row[col] ? 'Yes' : 'No')
                          : row[col]?.toString() || ''}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </Card.Body>
      </Card>
    );
  };

  // Stats renderer
  const renderStats = () => {
    if (!stats || Object.keys(stats).length === 0) return null;
    return (
      <Card className="mb-4">
        <Card.Header>
          <h5 className="mb-0">Stats</h5>
        </Card.Header>
        <Card.Body>
          <ul className="mb-0">
            {Object.entries(stats).map(([k, v]) => (
              <li key={k}><strong>{k}:</strong> {v}</li>
            ))}
          </ul>
        </Card.Body>
      </Card>
    );
  };

  // Main render
  return (
    <div className="upload-container">
      <Button
        className="theme-toggle"
        variant="outline-primary"
        onClick={() => setDarkMode((d) => !d)}
        aria-label="Toggle dark/light mode"
      >
        {darkMode ? <FiSun /> : <FiMoon />}
      </Button>
      <Container>
        <Row className="justify-content-center">
          <Col lg={10}>
            <h1 className="display-4 mb-4 text-center">AssetWise ML Prediction</h1>
            {error && <Alert variant="danger" onClose={() => setError(null)} dismissible>{error}</Alert>}
            {progressMsg && (
              <div className="loading-spinner">
                <Spinner animation="border" variant="primary" className="me-2" />
                <span>{progressMsg}</span>
              </div>
            )}

            {/* Upload area */}
            {!uuid && !loading && (
              <div {...getRootProps()} className={`upload-area mb-4${isDragActive ? ' dragover' : ''}`}>
                <input {...getInputProps()} />
                <FiUpload size={48} className="mb-3" />
                <h4>Drag &amp; drop a CSV/XLSX file here, or click to select</h4>
                <p className="text-muted">File will be previewed before prediction.</p>
              </div>
            )}

            {/* Preview and Predict */}
            {uuid && !finalResults && (
              <>
                <Card className="mb-4">
                  <Card.Body>
                    <div className="d-flex justify-content-between align-items-center">
                      <div>
                        <strong>File:</strong> {filename}
                      </div>
                      <Button
                        variant="outline-primary"
                        onClick={handleReset}
                        disabled={loading}
                      >
                        Upload New File
                      </Button>
                    </div>
                  </Card.Body>
                </Card>
                {/* Tabbed preview */}
                <Tab.Container activeKey={activeTab} onSelect={setActiveTab}>
                  <Nav variant="tabs" className="mb-3">
                    <Nav.Item>
                      <Nav.Link eventKey="raw">Raw Preview</Nav.Link>
                    </Nav.Item>
                  </Nav>
                  <Tab.Content>
                    <Tab.Pane eventKey="raw">
                      {renderTable(rawPreview, "Raw Data Preview")}
                    </Tab.Pane>
                  </Tab.Content>
                </Tab.Container>
                {renderStats()}
                <div className="d-flex justify-content-end">
                  <Button
                    variant="primary"
                    onClick={handlePredict}
                    disabled={loading}
                  >
                    {loading ? <Spinner size="sm" animation="border" className="me-2" /> : null}
                    Generate Prediction
                  </Button>
                </div>
              </>
            )}

            {/* Results */}
            {finalResults && (
              <>
                <Card className="mb-4">
                  <Card.Body>
                    <div className="d-flex justify-content-between align-items-center">
                      <div>
                        <strong>File:</strong> {filename}
                      </div>
                      <Button
                        variant="outline-primary"
                        onClick={handleReset}
                        disabled={loading}
                      >
                        Upload New File
                      </Button>
                    </div>
                  </Card.Body>
                </Card>
                <Tab.Container activeKey={activeTab} onSelect={setActiveTab}>
                  <Nav variant="tabs" className="mb-3">
                    <Nav.Item>
                      <Nav.Link eventKey="raw">Raw Preview</Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                      <Nav.Link eventKey="processed">Processed Preview</Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                      <Nav.Link eventKey="final">Prediction Results</Nav.Link>
                    </Nav.Item>
                  </Nav>
                  <Tab.Content>
                    <Tab.Pane eventKey="raw">
                      {renderTable(rawPreview, "Raw Data Preview")}
                    </Tab.Pane>
                    <Tab.Pane eventKey="processed">
                      {renderTable(processedPreview, "Processed Data Preview")}
                    </Tab.Pane>
                    <Tab.Pane eventKey="final">
                      {renderTable(finalResults, "Prediction Results")}
                    </Tab.Pane>
                  </Tab.Content>
                </Tab.Container>
                {renderStats()}
                <div className="d-flex gap-2 flex-wrap justify-content-end">
                  <Button
                    variant="outline-primary"
                    onClick={() => handleExport('csv')}
                    disabled={loading || !exportLinks.csv}
                  >
                    <FiDownload className="me-2" />
                    Export CSV
                  </Button>
                  <Button
                    variant="outline-primary"
                    onClick={() => handleExport('xlsx')}
                    disabled={loading || !exportLinks.xlsx}
                  >
                    <FiDownload className="me-2" />
                    Export XLSX
                  </Button>
                </div>
              </>
            )}
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default App;
