import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { Container, Row, Col, Card, Button, Alert, Spinner, Badge, Dropdown, ButtonGroup } from 'react-bootstrap';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import { saveAs } from 'file-saver';
import { FiUpload, FiDownload, FiDatabase, FiSun, FiMoon, FiClock, FiInfo } from 'react-icons/fi';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

// Import new components
import ProjectFilters from './components/ProjectFilters';
import DataStats from './components/DataStats';

function App() {
  const [fileData, setFileData] = useState(null);
  const [predictions, setPredictions] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [darkMode, setDarkMode] = useState(() => {
    // Persist mode in localStorage
    return localStorage.getItem('darkMode') === 'true';
  });
  const [showPreview, setShowPreview] = useState(true);
  const [fileInfo, setFileInfo] = useState(null);
  const [storageStats, setStorageStats] = useState(null);

  // Project filtering state
  const [selectedProject, setSelectedProject] = useState(null);
  const [availableProjects, setAvailableProjects] = useState([]);
  const [fullDataset, setFullDataset] = useState(null);
  const [filteredData, setFilteredData] = useState(null);
  const [filterLoading, setFilterLoading] = useState(false);
  const [previewNumRows, setPreviewNumRows] = useState(5);
  const [previewSection, setPreviewSection] = useState('both'); // 'head', 'tail', 'both'

  // After prediction, merge has_booked_prediction from full_dataset into processed_dataset for display
  const [predictionDisplayData, setPredictionDisplayData] = useState(null);

  useEffect(() => {
    // Fetch available models on component mount
    fetchAvailableModels();
    fetchStorageStats();
  }, []);

  useEffect(() => {
    document.body.classList.toggle('dark-mode', darkMode);
    document.body.classList.toggle('light-mode', !darkMode);
    localStorage.setItem('darkMode', darkMode);
  }, [darkMode]);

  useEffect(() => {
    if (
      predictions &&
      Array.isArray(predictions.processed_dataset) &&
      Array.isArray(predictions.full_dataset) &&
      predictions.processed_dataset.length === predictions.full_dataset.length &&
      predictions.processed_dataset.length > 0
    ) {
      const processed = predictions.processed_dataset;
      const predicted = predictions.full_dataset;
      const merged = processed.map((row, idx) => ({
        ...row,
        has_booked_prediction: predicted[idx]?.has_booked_prediction,
      }));
      setPredictionDisplayData(merged);
    } else {
      setPredictionDisplayData(null);
    }
  }, [predictions]);

  const fetchAvailableModels = async () => {
    try {
      const response = await axios.get('/api/models');
      // We'll use this data later if needed
      console.log('Available models:', response.data.available_models);
    } catch (error) {
      console.error('Error fetching models:', error);
    }
  };

  const fetchStorageStats = async () => {
    try {
      const response = await axios.get('/api/storage/stats');
      setStorageStats(response.data);
    } catch (error) {
      console.error('Error fetching storage stats:', error);
    }
  };

  // Extract unique projects from data
  const extractProjects = useCallback((data) => {
    if (!data || data.length === 0) return [];

    const projects = new Set();
    data.forEach(row => {
      if (row.projectid) {
        projects.add(row.projectid.toString());
      }
    });

    return Array.from(projects);
  }, []);

  // Filter data by project
  const filterDataByProject = useCallback((data, projectId) => {
    if (!data || !projectId) return data;
    setFilterLoading(true);
    setTimeout(() => {
      const filtered = data.filter(row =>
        row.projectid && row.projectid.toString() === projectId.toString()
      );
      setFilteredData(filtered);
      setFilterLoading(false);
    }, 0);
  }, []);

  // Handle project selection
  const handleProjectFilter = useCallback((projectId) => {
    setSelectedProject(projectId);
    if (projectId) {
      if (predictions && predictionDisplayData) {
        filterDataByProject(predictionDisplayData, projectId);
      } else {
        filterDataByProject(fullDataset, projectId);
      }
    } else {
      setFilteredData(predictions && predictionDisplayData ? predictionDisplayData : fullDataset);
    }
  }, [fullDataset, filterDataByProject, predictions, predictionDisplayData]);

  // Handle filter reset
  const handleFilterReset = useCallback(() => {
    setSelectedProject(null);
    setFilteredData(predictions && predictionDisplayData ? predictionDisplayData : fullDataset);
  }, [fullDataset, predictions, predictionDisplayData]);

  // Memoized current data (either filtered or full dataset)
  const currentData = useMemo(() => {
    if (predictions && predictionDisplayData) {
      return selectedProject
        ? predictionDisplayData.filter(row => row.projectid && row.projectid.toString() === selectedProject.toString())
        : predictionDisplayData;
    } else {
      return selectedProject ? filteredData : fullDataset;
    }
  }, [selectedProject, filteredData, fullDataset, predictions, predictionDisplayData]);

  // --- Preview Controls ---
  const previewOptions = [5, 10, 50];
  const sectionOptions = [
    { value: 'head', label: 'Head' },
    { value: 'tail', label: 'Tail' },
    { value: 'both', label: 'Head & Tail' },
  ];

  // --- Updated previewRows logic ---
  const previewRows = useMemo(() => {
    if (!currentData || currentData.length === 0) return [];
    const n = previewNumRows;
    if (previewSection === 'head') {
      return currentData.slice(0, n);
    } else if (previewSection === 'tail') {
      return currentData.slice(-n);
    } else { // both
      if (currentData.length <= 2 * n) {
        return currentData;
      }
      const firstN = currentData.slice(0, n);
      const lastN = currentData.slice(-n);
      return [...firstN, ...lastN];
    }
  }, [currentData, previewNumRows, previewSection]);

  // Use predictionDisplayData for previewRows after prediction
  const predictionRows = useMemo(() => {
    if (!predictionDisplayData) return [];
    const n = previewNumRows;
    if (previewSection === 'head') {
      return predictionDisplayData.slice(0, n);
    } else if (previewSection === 'tail') {
      return predictionDisplayData.slice(-n);
    } else {
      if (predictionDisplayData.length <= 2 * n) {
        return predictionDisplayData;
      }
      const firstN = predictionDisplayData.slice(0, n);
      const lastN = predictionDisplayData.slice(-n);
      return [...firstN, ...lastN];
    }
  }, [predictionDisplayData, previewNumRows, previewSection]);

  const onDrop = async (acceptedFiles) => {
    if (acceptedFiles.length === 0) return;

    const file = acceptedFiles[0];
    setLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('/api/upload_uuid', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // Cache the full dataset for filtering (all rows)
      setFullDataset(response.data.full_dataset);
      setFilteredData(response.data.full_dataset);

      // Extract available projects from the full dataset
      const projects = extractProjects(response.data.full_dataset);
      setAvailableProjects(projects);

      setFileData({
        filename: response.data.filename,
        preview: response.data.preview,  // Keep preview for display
        originalName: file.name,
      });
      setFileInfo(response.data.file_info);
      setShowPreview(true);

      // Reset filter state
      setSelectedProject(null);

      console.log('Uploaded fileData:', {
        filename: response.data.filename,
        previewRows: response.data.preview.length,
        fullDatasetRows: response.data.full_dataset.length,
        originalName: file.name,
        fileInfo: response.data.file_info,
        availableProjects: projects,
      });
    } catch (error) {
      setError(error.response?.data?.error || 'Error uploading file');
    } finally {
      setLoading(false);
    }
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'text/csv': ['.csv'],
      'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet': ['.xlsx'],
      'application/vnd.ms-excel': ['.xls'],
    },
    multiple: false,
  });

  const generatePredictions = async () => {
    if (!fileData) return;
    setLoading(true);
    setError(null);
    try {
      const response = await axios.post('/api/predict_workflow', {
        filename: fileData.filename,
      });
      console.log('Prediction response:', response.data);

      // Cache the full prediction dataset for filtering (all rows)
      const fullPredictionData = response.data.full_dataset;
      setFullDataset(response.data.processed_dataset);
      setFilteredData(response.data.processed_dataset);

      // Re-extract projects from prediction data (in case preprocessing changed project IDs)
      const projects = extractProjects(response.data.full_dataset);
      setAvailableProjects(projects);

      setPredictions(response.data);
      setShowPreview(false); // Hide preview after prediction

      // Reset filter state for prediction results
      setSelectedProject(null);
    } catch (error) {
      setError(error.response?.data?.error || 'Error generating predictions');
    } finally {
      setLoading(false);
    }
  };

  // --- Export logic ---
  const exportResults = async (format) => {
    if (!predictions) return;
    try {
      const response = await axios.get(`/api/export/${format}/${predictions.results_filename}`, {
        responseType: 'blob',
      });
      let extension = format;
      if (format === 'xlsx') extension = 'xlsx';
      if (format === 'csv') extension = 'csv';
      if (format === 'json') extension = 'json';
      saveAs(response.data, `predictions_${fileData.filename.split('.')[0]}.${extension}`);
    } catch (error) {
      setError('Error exporting results');
    }
  };

  const cleanup = async () => {
    setFileData(null);
    setPredictions(null);
    setFileInfo(null);
    setError(null);
    setShowPreview(true);

    // Reset filter state
    setSelectedProject(null);
    setAvailableProjects([]);
    setFullDataset(null);
    setFilteredData(null);
    setFilterLoading(false);
  };

  const renderPreviewTable = (data, title) => {
    if (!data || data.length === 0) return null;

    let columns = Object.keys(data[0]);
    if (columns.includes('has_booked_prediction')) {
      columns = columns.filter(c => c !== 'has_booked_prediction').concat(['has_booked_prediction']);
    }

    return (
      <Card className="mb-4">
        <Card.Header>
          <h5 className="mb-0">
            {title}
            {filterLoading && (
              <Spinner animation="border" size="sm" className="ms-2" />
            )}
          </h5>
        </Card.Header>
        <Card.Body className="p-0">
          <div className="table-responsive">
            <table className="table table-striped table-hover mb-0">
              <thead className="table-primary">
                <tr>
                  {columns.map((column) => (
                    <th key={column}>{column}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {data.map((row, index) => (
                  <tr key={index}>
                    {columns.map((column) => (
                      <td key={column}>
                        {typeof row[column] === 'boolean' 
                          ? (row[column] ? 'Yes' : 'No')
                          : row[column]?.toString() || ''}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </Card.Body>
      </Card>
    );
  };

  // --- Preview controls UI ---
  const renderPreviewControls = () => (
    <div className="d-flex align-items-center gap-3 mb-2">
      <span>Preview:</span>
      <Dropdown as={ButtonGroup} onSelect={val => setPreviewNumRows(Number(val))}>
        <Button variant="outline-primary">{previewNumRows} rows</Button>
        <Dropdown.Toggle split variant="outline-primary" id="dropdown-split-basic" />
        <Dropdown.Menu>
          {previewOptions.map(opt => (
            <Dropdown.Item key={opt} eventKey={opt}>{opt} rows</Dropdown.Item>
          ))}
        </Dropdown.Menu>
      </Dropdown>
      <Dropdown as={ButtonGroup} onSelect={val => setPreviewSection(val)}>
        <Button variant="outline-secondary">{sectionOptions.find(o => o.value === previewSection)?.label}</Button>
        <Dropdown.Toggle split variant="outline-secondary" id="dropdown-split-section" />
        <Dropdown.Menu>
          {sectionOptions.map(opt => (
            <Dropdown.Item key={opt.value} eventKey={opt.value}>{opt.label}</Dropdown.Item>
          ))}
        </Dropdown.Menu>
      </Dropdown>
    </div>
  );

  const renderFileRetentionInfo = () => {
    if (!fileInfo) return null;

    const uploadDate = new Date(fileInfo.upload_timestamp);
    const deletionDate = new Date(fileInfo.deletion_date);
    const daysLeft = Math.ceil((deletionDate - new Date()) / (1000 * 60 * 60 * 24));

    return (
      <Card className="mb-4">
        <Card.Header>
          <h5 className="mb-0">
            <FiClock className="me-2" />
            File Retention Information
          </h5>
        </Card.Header>
        <Card.Body>
          <Row>
            <Col md={6}>
              <p><strong>Upload Time:</strong> {uploadDate.toLocaleString()}</p>
              <p><strong>Deletion Date:</strong> {deletionDate.toLocaleString()}</p>
            </Col>
            <Col md={6}>
              <p><strong>Days Remaining:</strong> 
                <Badge 
                  bg={daysLeft > 30 ? 'success' : daysLeft > 7 ? 'warning' : 'danger'}
                  className="ms-2"
                >
                  {daysLeft} days
                </Badge>
              </p>
              <p><strong>Status:</strong> 
                <Badge bg="info" className="ms-2">{fileInfo.status}</Badge>
              </p>
            </Col>
          </Row>
        </Card.Body>
      </Card>
    );
  };

  const renderStorageStats = () => {
    if (!storageStats) return null;

    return (
      <Card className="mb-4">
        <Card.Header>
          <h5 className="mb-0">
            <FiInfo className="me-2" />
            Storage Statistics
          </h5>
        </Card.Header>
        <Card.Body>
          <Row>
            <Col md={3}>
              <div className="text-center">
                <div className="stats-number text-primary">{storageStats.total_files}</div>
                <div className="text-muted">Total Files</div>
              </div>
            </Col>
            <Col md={3}>
              <div className="text-center">
                <div className="stats-number text-info">{storageStats.total_size_mb} MB</div>
                <div className="text-muted">Total Size</div>
              </div>
            </Col>
            <Col md={3}>
              <div className="text-center">
                <div className="stats-number text-success">{storageStats.retention_days}</div>
                <div className="text-muted">Retention Days</div>
              </div>
            </Col>
            <Col md={3}>
              <div className="text-center">
                <Button 
                  variant="outline-secondary" 
                  size="sm"
                  onClick={fetchStorageStats}
                >
                  <FiInfo className="me-1" />
                  Refresh
                </Button>
              </div>
            </Col>
          </Row>
        </Card.Body>
      </Card>
    );
  };

  return (
    <Container fluid className="upload-container">
      <button
        className="theme-toggle"
        onClick={() => setDarkMode((dm) => !dm)}
        title={darkMode ? 'Switch to Light Mode' : 'Switch to Dark Mode'}
      >
        {darkMode ? <FiMoon /> : <FiSun />}
      </button>
      <Row className="justify-content-center">
        <Col lg={10}>
          <div className="text-center mb-4">
            <h1 className="display-4 text-primary">ML Prediction System</h1>
            <p className="lead text-muted">
              Upload your CSV or Excel files to get predictions using our pre-trained models
            </p>
          </div>

          {error && (
            <Alert variant="danger" dismissible onClose={() => setError(null)}>
              {error}
            </Alert>
          )}

          {!fileData && (
            <Card>
              <Card.Body>
                <div
                  {...getRootProps()}
                  className={`upload-area ${isDragActive ? 'dragover' : ''}`}
                >
                  <input {...getInputProps()} />
                  <FiUpload size={48} className="text-primary mb-3" />
                  <h4>Drop your file here, or click to select</h4>
                  <p className="text-muted">
                    Supports CSV and Excel files (max 100,000 rows) - Files are automatically converted to CSV for faster processing
                  </p>
                  <p className="text-muted small">
                    File must contain a "projectid" column
                  </p>
                </div>
              </Card.Body>
            </Card>
          )}

          {loading && (
            <div className="loading-spinner">
              <div className="text-center">
                <Spinner animation="border" role="status" className="mb-3">
                  <span className="visually-hidden">Loading...</span>
                </Spinner>
                <p>Processing your file...</p>
              </div>
            </div>
          )}

          {fileData && !loading && showPreview && (
            <>
              <Card className="mb-4">
                <Card.Header>
                  <h5 className="mb-0">File Information</h5>
                </Card.Header>
                <Card.Body>
                  <Row>
                    <Col md={6}>
                      <p><strong>Filename:</strong> {fileData.originalName || fileData.filename}</p>
                    </Col>
                    <Col md={6}>
                      <Button
                        variant="primary"
                        onClick={generatePredictions}
                        disabled={loading || !fileData}
                        className="me-2"
                      >
                        <FiDatabase className="me-2" />
                        Generate Predictions
                      </Button>
                      <Button variant="outline-secondary" onClick={cleanup}>
                        Upload New File
                      </Button>
                    </Col>
                  </Row>
                </Card.Body>
              </Card>

              {/* Project Filters */}
              <ProjectFilters
                projects={availableProjects}
                selectedProject={selectedProject}
                onSelect={handleProjectFilter}
                onReset={handleFilterReset}
                disabled={loading}
                className="mb-4"
              />

              {/* Data Statistics */}
              <DataStats
                data={predictionDisplayData}
                stats={predictions ? predictions.stats : {}}
                selectedProject={selectedProject}
                title={selectedProject ? `Project ${selectedProject} Prediction Statistics` : "Full Prediction Statistics"}
                className="mb-4"
              />

              {renderPreviewControls()}
              {/* Preview Table */}
              {(!previewRows || previewRows.length === 0) ? (
                <Alert variant="warning">No preview data available for this file.</Alert>
              ) : (
                renderPreviewTable(previewRows, `Data Preview ${selectedProject ? `(Project ${selectedProject})` : '(All Projects)'}`)
              )}
            </>
          )}

          {predictions && !loading && (
            <>
              <Card className="mb-4">
                <Card.Header>
                  <h5 className="mb-0">Prediction Results</h5>
                </Card.Header>
                <Card.Body>
                  <Row>
                    <Col md={6}>
                      <p><strong>Rows Processed:</strong> {predictions.stats?.rows_processed ?? '-'}</p>
                      <p><strong>Prediction Distribution:</strong> {JSON.stringify(predictions.stats?.prediction_distribution ?? {})}</p>
                    </Col>
                  </Row>
                </Card.Body>
              </Card>

              {/* Project Filters for Prediction Results */}
              <ProjectFilters
                projects={availableProjects}
                selectedProject={selectedProject}
                onSelect={handleProjectFilter}
                onReset={handleFilterReset}
                disabled={loading}
                className="mb-4"
              />

              {/* Prediction Data Statistics */}
              <DataStats
                data={predictionDisplayData}
                stats={predictions ? predictions.stats : {}}
                selectedProject={selectedProject}
                title={selectedProject ? `Project ${selectedProject} Prediction Statistics` : "Full Prediction Statistics"}
                className="mb-4"
              />

              {renderPreviewControls()}
              {/* Final Prediction Preview */}
              <h5>Final Prediction Preview</h5>
              {renderPreviewTable(predictionRows, `Prediction Results ${selectedProject ? `(Project ${selectedProject})` : '(All Projects)'}`)}

              <Card>
                <Card.Header>
                  <h5 className="mb-0">
                    <FiDownload className="me-2" />
                    Export Results
                  </h5>
                </Card.Header>
                <Card.Body>
                  <div className="d-flex flex-wrap gap-2">
                    <Button
                      variant="outline-primary"
                      onClick={() => exportResults('csv')}
                      className="btn-export"
                    >
                      Export as CSV
                    </Button>
                    <Button
                      variant="outline-success"
                      onClick={() => exportResults('xlsx')}
                      className="btn-export"
                    >
                      Export as Excel
                    </Button>
                    <Button
                      variant="outline-info"
                      onClick={() => exportResults('json')}
                      className="btn-export"
                    >
                      Export as JSON
                    </Button>
                  </div>
                </Card.Body>
              </Card>
              {/* Upload New File button at the bottom */}
              <div style={{ display: 'flex', justifyContent: 'center', marginTop: '2rem', marginBottom: '1rem' }}>
                <Button
                  variant="secondary"
                  size="lg"
                  onClick={cleanup}
                  style={{ minWidth: 220, fontWeight: 600 }}
                >
                  Upload New File
                </Button>
              </div>
            </>
          )}

          {fileInfo && (
            <>
              {renderFileRetentionInfo()}
              {renderStorageStats()}
            </>
          )}
        </Col>
      </Row>
    </Container>
  );
}

export default App;
